package helper

// StaticHTML returns a hardcoded HTML page for the application.
func StaticHTML() string {
	return `<!DOCTYPE html>
	<html>
		<head>
			<title>Sudoku Solver</title>
			<style>
				.grid {
					display: grid;
					grid-template-columns: repeat(3, 180px);
					grid-template-rows: repeat(3, 180px);
				}
				.box {
					border: 1px solid rgba(0, 0, 0, 0.8);
					display: grid;
					grid-template-columns: repeat(3, 60px);
					grid-template-rows: repeat(3, 60px);
				}
				.cell {
					border: 1px solid rgba(0, 0, 0, 0.3);
					font-size: 40px;
					text-align: center;
					justify-content: center;
					vertical-align: bottom;
					padding: 5px;
				}
				#main {
					visibility: hidden;
				}
				#loading {
					visibility: visible;
				}
				#error {
					visibility: hidden;
				}
			</style>
		</head>
		<body onload="initPage()">
			<h1>Sudoku Solver</h1>
			<div id="loading">
				<center>
					<!-- spinning loader image generated by http://www.ajaxload.info/ -->
					<img src="data:image/gif;base64," /><span style="font-size: 3em;">&nbsp;Loading...</span>
				</center>
			</div>
			<div id="error">
				<h2>Error</h2>
				<pre id="errormessage"></pre>
			</div>
			<div id="main">
				<input id="show" type="checkbox" onclick="populateGrid()"/>Show Answers
				<div id="grid" class="grid">
				</div>
			</div>
			<script type="text/javascript">
				var reply;
	
				function loadData() {
					var xmlhttp = new XMLHttpRequest();
					xmlhttp.onreadystatechange = function() {
					  if (this.readyState == 4) {
						if (this.status == 200) {
							try {
								var loading = document.getElementById("loading");
								loading.style.visibility="hidden";
								loading.style.display="none";
	
								reply = JSON.parse(this.responseText);
								
								if (reply.error != null) {
									document.getElementById("errormessage").innerText = reply.error;
									document.getElementById("error").visibility="visible";
									return;
								}
								document.getElementById("error").style.display="none";
								document.getElementById("main").style.visibility="visible";
								populateGrid();
								return;
						  } catch (e) {
							  document.getElementById("errormessage").innerText = "Got an unexpected response while fetching JSON: " + e;
							  document.getElementById("error").style.visibility="visible";
							  document.getElementById("loading").style.visibility="hidden";
							  return;
						  }
						} else {
							  document.getElementById("errormessage").innerText = "Got an unexpected response while fetching JSON: " + this.status;
							  document.getElementById("error").style.visibility="visible";
							  document.getElementById("loading").style.visibility="hidden";
							  return;
						}
					  }
					}
					xmlhttp.open("GET", "/puzzle", true);
					xmlhttp.send();
				}
	
				function initPage() {
					document.getElementById("main").style.visibility="hidden";
					document.getElementById("error").style.visibility="hidden";
					document.getElementById("loading").style.visibility="visible";
					buildGrid();
					loadData();
				}
	
				function buildGrid() {
					var b = 1;
					var boxStart;
					var s;
					var grid = document.getElementById("grid");
					for (var b=0; b<9; b++) {
						var box = document.createElement("div");
						box.className = "box";
						box.id = 'box' + b;
						boxStart = parseInt(b/3)*27 + (b%3)*3;
						for (var i=0; i<9; i++) {
							var index = boxStart + parseInt(i/3)*6 + i;
							var cell = document.createElement("div");
							cell.className = "cell";
							cell.id = 'cell' + index;
							box.appendChild(cell);
						}
						grid.appendChild(box);
					}
				}
	
				function populateGrid() {
					var content;
					var s;
					var cell;
					console.log(reply);
					if (document.getElementById("show").checked == true) {
						content = reply.answer;
					} else {
						content = reply.question;
					}
					for (var i=0; i<81; i++) {
						cell = document.getElementById("cell" + i);
						s = content.charAt(i);
						if (s == "0") { s = ""; }
						cell.innerText = s;
					}
				}
			</script>
		</body>
	</html>`
}
